YData3 = massInversePatch;
YData4 = branchInversePatch;


%  YDATA1:  patch ydata
%  XDATA1:  patch xdata
%  YDATA2:  patch ydata
%  X1:  vector of x data
%  YMATRIX1:  matrix of y data
%  YDATA3:  patch ydata
%  YDATA4:  patch ydata

%  Auto-generated by MATLAB on 25-Jul-2020 08:36:40

