figure('Name','Global Coral Cover','OuterPosition',[1 1 1177 715]);

% Create axes
axes1 = axes;
hold(axes1,'on');

% Create patch
patch('DisplayName','25th - 75th percentile','YData',YData1,'XData',XData1,...
    'FaceAlpha',0.2,...
    'LineStyle','none',...
    'FaceColor',[1 0.5 0.5]);

% Create patch
