    'FaceAlpha',0.2,...
    'LineStyle','none',...
    'FaceColor',[0.749019622802734 0 0.749019622802734]);

% Create multiple lines using matrix input to plot
plot1 = plot(X1,YMatrix1,'LineWidth',2);
set(plot1(1),'DisplayName','Mounding, 20% start','Color',[1 0 0]);
set(plot1(4),'DisplayName','Mounding, inverse start',...
    'Color',[0.466666668653488 0.674509823322296 0.18823529779911]);
set(plot1(2),'DisplayName','Branching, 80% Start',...
